import fs from 'node:fs';
import path from 'node:path';
import { transformFile } from '../../../utils/transform.mjs';

const toPackageJson = () => {
  const content = `{
  "name": "dot-mdxlayer",
  "description": "This package is auto-generated by mdxlayer",
  "version": "0.0.0",
  "exports": {
    "./generated": {
      "import": "./generated/index.mjs",
      "require": "./generated/index.cjs"
    }
  },
  "typesVersions": {
    "*": {
      "generated": [
        "./generated"
      ]
    }
  }
}`;
  const pkgPath = path.resolve(process.cwd(), ".mdxlayer", "package.json");
  const isChanged = !fs.existsSync(pkgPath);
  if (isChanged) {
    transformFile({
      doc: content,
      filename: "package.json",
      subpath: "/"
    });
  }
};

export { toPackageJson };
